use serde::{Deserialize, Serialize};
use yewdux::prelude::*;

#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
#[store(storage = "local")]
pub struct GlobalState {
    pub username: String,
    pub job_title: String,
    pub job_description: String,
    pub email: String,
    pub dob: String,
    pub address: String,
    pub gender: Gender,
    pub website: String,
    pub phone: String,
    pub work_experience: Vec<Experience>,
    pub education: Vec<Education>,
    pub skills: Vec<Skill>,
    pub recent_work: Vec<RecentWork>,
    pub socials: Vec<Social>,
    pub image_data: String,
}

#[derive(Debug, Default, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub struct Experience {
    pub position: String,
    pub explanation: String,
}

#[derive(Debug, Default, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub struct Education {
    pub position: String,
    pub explanation: String,
}

#[derive(Debug, Default, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub struct RecentWork {
    pub project: String,
    pub explanation: String,
    pub link: String,
}


#[derive(Debug, Default, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub struct Skill {
    pub skill: String,
    pub proficiency: String,
}

#[derive(Debug, Default, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub struct Social {
    pub name: String,
    pub link: String,
}

#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize, Store)]
pub enum Gender {
    DontWantToSay,
    Other,
    Female,
    Male,
}

